{"payload":{"allShortcutsEnabled":false,"fileTree":{"":{"items":[{"name":"LICENSE","path":"LICENSE","contentType":"file"},{"name":"README.md","path":"README.md","contentType":"file"},{"name":"adlogin.ps1","path":"adlogin.ps1","contentType":"file"},{"name":"localbrute-extra-mini.ps1","path":"localbrute-extra-mini.ps1","contentType":"file"},{"name":"localbrute.ps1","path":"localbrute.ps1","contentType":"file"},{"name":"port-scan-tcp-compat.ps1","path":"port-scan-tcp-compat.ps1","contentType":"file"},{"name":"port-scan-tcp.ps1","path":"port-scan-tcp.ps1","contentType":"file"},{"name":"port-scan-udp.ps1","path":"port-scan-udp.ps1","contentType":"file"},{"name":"smblogin-extra-mini.ps1","path":"smblogin-extra-mini.ps1","contentType":"file"},{"name":"smblogin.ps1","path":"smblogin.ps1","contentType":"file"}],"totalCount":10}},"fileTreeProcessingTime":5.7522709999999995,"foldersToFetch":[],"reducedMotionEnabled":null,"repo":{"id":262841920,"defaultBranch":"master","name":"Minimalistic-offensive-security-tools","ownerLogin":"InfosecMatter","currentUserCanPush":false,"isFork":false,"isEmpty":false,"createdAt":"2020-05-10T17:40:31.000Z","ownerAvatar":"https://avatars.githubusercontent.com/u/60963123?v=4","public":true,"private":false,"isOrgOwned":false},"symbolsExpanded":false,"treeExpanded":true,"refInfo":{"name":"master","listCacheKey":"v0:1589132434.0","canEdit":false,"refType":"branch","currentOid":"9537cbff1c7f6b15d1ed98da0a5dfaf9f8487940"},"path":"port-scan-udp.ps1","currentUser":null,"blob":{"rawLines":["Function port-scan-udp {\r","  param($hosts,$ports)\r","  if (!$ports) {\r","    Write-Host \"usage: port-scan-udp <host|hosts> <port|ports>\"\r","    Write-Host \" e.g.: port-scan-udp 192.168.1.2 445`n\"\r","    return\r","  }\r","  $out = \".\\scanresults.txt\"\r","  foreach($p in [array]$ports) {\r","   foreach($h in [array]$hosts) {\r","    $x = (gc $out -EA SilentlyContinue | select-string \"^$h,udp,$p,\")\r","    if ($x) {\r","      gc $out | select-string \"^$h,udp,$p,\"\r","      continue\r","    }\r","    $msg = \"$h,udp,$p,\"\r","    $u = new-object system.net.sockets.udpclient\r","    $u.Client.ReceiveTimeout = 500\r","    $u.Connect($h,$p)\r","    # Send a single byte 0x01\r","    [void]$u.Send(1,1)\r","    $l = new-object system.net.ipendpoint([system.net.ipaddress]::Any,0)\r","    $r = \"Filtered\"\r","    try {\r","      if ($u.Receive([ref]$l)) {\r","        # We have received some UDP data from the remote host in return\r","        $r = \"Open\"\r","      }\r","    } catch {\r","      if ($Error[0].ToString() -match \"failed to respond\") {\r","        # We haven't received any UDP data from the remote host in return\r","        # Let's see if we can ICMP ping the remote host\r","        if ((Get-wmiobject win32_pingstatus -Filter \"address = '$h' and Timeout=1000 and ResolveAddressNames=false\").StatusCode -eq 0) {\r","          # We can ping the remote host, so we can assume that ICMP is not\r","          # filtered. And because we didn't receive ICMP port-unreachable before,\r","          # we can assume that the remote UDP port is open\r","          $r = \"Open\"\r","        }\r","      } elseif ($Error[0].ToString() -match \"forcibly closed\") {\r","        # We have received ICMP port-unreachable, the UDP port is closed\r","        $r = \"Closed\"\r","      }\r","    }\r","    $u.Close()\r","    $msg += $r\r","    Write-Host \"$msg\"\r","    echo $msg >>$out\r","   }\r","  }\r","}\r","\r","# Examples:\r","#\r","# port-scan-udp 10.10.0.1 137\r","# port-scan-udp 10.10.0.1 (135,137,445)\r","# port-scan-udp (gc .\\ips.txt) 137\r","# port-scan-udp (gc .\\ips.txt) (135,137,445)\r","# 0..255 | foreach { port-scan-udp 10.10.0.$_ 137 }\r","# 0..255 | foreach { port-scan-udp 10.10.0.$_ (135,137,445) }\r"],"stylingDirectives":[[{"start":0,"end":8,"cssClass":"pl-k"},{"start":9,"end":22,"cssClass":"pl-en"}],[{"start":2,"end":7,"cssClass":"pl-k"},{"start":8,"end":14,"cssClass":"pl-smi"},{"start":14,"end":15,"cssClass":"p